import './App.css'
import Saudacao from './components/Saudacao'
import SeuNome from './components/SeuNome'


function App() {
  const [nome, setNome ] = UseState()

return (

  <div className ="App">
    <h1> State Lift</h1>
  <SeuNome setNome={setNome} />
  <Saudacao nome={nome}/>
  </div>
)

}

export default App;
