    e.preventDefault() 
    setUserEmail(email)
    console.log
}

    return (
          

        <div>

            <h2> Cadastre o seu e-mail:</h2>
            <form>
                <input type = "email" placeholder="Digite seu e-mail..." onChange={(e) => setEmail(e.target.value)}/>
                
